import React from 'react';
import './App.css';
import Sidebar from './Sidebar';
import Cart from './Cart';
import CourseArea from './CourseArea';
import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'
import RecommendSidebar from './RecommendSidebar.js'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      allCourses: {},
      filteredCourses: {},
      cartCourses: {},
      previousCourses: [],
      previousCourseNames: {},
      recommededCourses: {},
      subjects: []
    };
  }

  componentDidMount() {
    fetch('https://mysqlcs639.cs.wisc.edu:5000/classes').then(
      res => res.json()
    ).then(data => this.setState({allCourses: data, filteredCourses: data, subjects: this.getSubjects(data)}));
    fetch('https://mysqlcs639.cs.wisc.edu/students/5022025924/classes/completed').then(
      res => res.json()
    ).then(data => this.setState({previousCourseNames: data}));
  }

  getSubjects(data) {
    let subjects = [];
    subjects.push("All");

    for(const course of Object.values(data)) {
      if(subjects.indexOf(course.subject) === -1)
        subjects.push(course.subject);
    }

    return subjects;
  }

  setCourses(courses) {
    this.setState({filteredCourses: courses})
  }

  setCartCourses(data) {
    this.setState({cartCourses: data});
  }

  setPreviousCourses = (previous) => {
    this.setState({previousCourses: previous})
  }

  render() {
    return (
      <>
        <link
          rel="stylesheet"
          href="https://maxcdn.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css"
          integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T"
          crossOrigin="anonymous"
        />
        <Tabs defaultActiveKey="Search" id="tabs-home">
          <Tab eventKey="Search" title="Course Search">
            <Sidebar setCourses={(courses) => this.setCourses(courses)} courses={this.state.allCourses} subjects={this.state.subjects}/>
            <div style={{marginLeft: '20vw'}}>
              <CourseArea data={this.state.filteredCourses}/>
            </div>
          </Tab>
          <Tab eventKey="Cart" title="Cart">
            <Cart setCartCourses={(courses) => this.setCartCourses(courses)} courses={this.state.allCourses} subjects={this.state.subjects} />
            <div style={{marginLeft: '20vw'}}>
              <CourseArea data={this.state.cartCourses}/>
            </div>
          </Tab>
          <Tab eventKey="Recommend" title="Recommened Courses">
          {/* setPreviousCourses={(courses, previousCourseNames) => this.setPreviousCourses(courses, previousCourseNames)} */}
            <RecommendSidebar setPreviousCourses={this.setPreviousCourses} previousCourseNames={this.state.previousCourseNames} courses={this.state.allCourses}/>
            <div style={{marginLeft: '20vw'}}>
              {/* <CourseArea data={this.state.previousCourses}/> */}
            </div>
          </Tab>
        </Tabs>
      </>
    )
  }
}

export default App;
