import React from 'react';
import './App.css';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion'
import Button from 'react-bootstrap/Button'

class PreviousCourse extends React.Component {

  thumbsUpClick = () =>{
    console.log("Thumbs Up Clicked");
    this.props.setLikedCourses(this.props.data);
    document.getElementById(this.props.key).style.border = "green"
  }

  thumbsDownClick = () =>{
    console.log("Thumbs Down Clicked");
    this.props.setDislikedCourses(this.props.data);
    document.getElementById(this.props.key).style.border = "red"
  }

  // setLikedCourses(){
  //   this.props.setLikedCourses(this.setLikedCourses(this.props.data));
  // }
  // setDislikedCourses(){
  //   this.props.setDislikedCourses(this.props.data);
  // }

  render() {
    return (
      <Accordion id={this.props.key}>
        <Card style={{width: '100%', marginTop: '5px', marginBottom: '5px'}}>
          <Card.Header>
            <Button style={{right: "5px", paddingRight: "5px", marginRight: "5px"}} variant="success" onClick={this.thumbsUpClick}>ğŸ‘</Button>
            <Button style={{right: "5px", paddingRight: "5px", marginRight: "5px"}} variant="danger" onClick={this.thumbsDownClick}>ğŸ‘</Button>
            <Accordion.Toggle as={Button} variant="link" eventKey="0" style={{fontWeight:"bold"}}>{this.props.data.name}
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body className="mb-2 text-muted">
              Number: {this.props.data.number} <br></br> 
              Credits: {this.props.data.credits} <br></br> 
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }
}

export default PreviousCourse;
