import React from 'react';
import './App.css';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion'
import Button from 'react-bootstrap/Button'

class Course extends React.Component {
  render() {
    return (
      <Accordion>
        <Card style={{width: '100%', marginTop: '5px', marginBottom: '5px'}}>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">{this.props.data.name}
              <Button style={{position: "absolute",right: "5px", padding: "5px"}} variant="outline-primary">Add to Cart</Button>
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body className="mb-2 text-muted">
              Number: {this.props.data.number} <br></br> 
              Credits: {this.props.data.credits} <br></br> 
              Discription: {this.props.data.description} <br></br> 
              <br></br> 
              Sections: <br></br>
            </Card.Body>
            
            {/* add add to cart and section info here */}
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }

  getCredits() {
    if(this.props.data.credits === 1)
      return '1 credit';
    else
      return this.props.data.credits + ' credits';
  }

  getSections(){
    // add to some structure here
    for(const section of this.props.data.sections){
      this.makeSection(section);
    }
    return
  }

  makeSection(section){
    console.log(section);
    return (
      <Accordion>
        <Card style={{width: '100%', marginTop: '5px', marginBottom: '5px'}}>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">{section}</Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body className="mb-2 text-muted">
              Instructor: {section.instructor} <br></br> 
              Location: {section.location} <br></br> 
              Time: {section.time} <br></br> 
              <Button style={{position: "absolute",right: "5px", padding: "5px"}} variant="outline-primary">Add to Cart</Button>
            </Card.Body>
            {/* add add to cart and section info here */}
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }
  makeSubsection(subsection){


  }
}

export default Course;
