import React from 'react';
import './App.css';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion'
import Button from 'react-bootstrap/Button'

class Course extends React.Component {
  render() {
    return (
      <Accordion>
        <Card style={{width: '33%', marginTop: '5px', marginBottom: '5px'}}>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">{this.props.data.name}</Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body className="mb-2 text-muted">Number: {this.props.data.number} <br></br> Credits: {this.props.data.credits} <br></br> Discription: {this.props.data.description} </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }

  getCredits() {
    if(this.props.data.credits === 1)
      return '1 credit';
    else
      return this.props.data.credits + ' credits';
  }
}

export default Course;
