import React from 'react';
import './App.css';
import Card from 'react-bootstrap/Card';
import Accordion from 'react-bootstrap/Accordion'
import Button from 'react-bootstrap/Button'

class Course extends React.Component {
  render() {
    return (
      <Accordion>
        <Card style={{width: '100%', marginTop: '5px', marginBottom: '5px'}}>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">{this.props.data.name}
              {/* add to cart fuctionality */}
              <Button style={{position: "absolute",right: "5px", padding: "5px"}} variant="outline-primary">Add to Cart</Button>
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body className="mb-2 text-muted">
              Number: {this.props.data.number} <br></br> 
              Credits: {this.props.data.credits} <br></br> 
              Discription: {this.props.data.description} <br></br> 
              <br></br> 
              Sections: <br></br>
              {this.getSections(this.props.data.sections)}
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }

  getCredits() {
    if(this.props.data.credits === 1)
      return '1 credit';
    else
      return this.props.data.credits + ' credits';
  }

  getSections(sections){
    // add to some structure here
    let allSections = [];
    for(const section of Object.entries(sections)){
      allSections.push(this.makeSection(section));
    }
    return allSections;
  }

  makeSection(section){
    return (
      <Accordion>
        <Card style={{width: '100%', marginTop: '5px', marginBottom: '5px'}}>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">{}
              {/* add to cart fuctionality */}
              <Button style={{position: "absolute",right: "5px", padding: "5px"}} variant="outline-primary">Add to Cart</Button>
              {/* onClick setCartCourses = {data} => this.props.section} */}
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body className="mb-2 text-muted">
              Instructor: {section.instructor} <br></br> 
              Location: {section.location} <br></br> 
              {/* Time: {section.time} <br></br> */}
            </Card.Body>
            {this.getSubsections(section.subsections)}
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }

  getSubsections(subsections){
    // // add to some structure here
    // let allSubsections = [];
    // for(const subsection of Object.values(subsections)){
    //   allSubsections.push(this.makeSubsection(subsection));
    // }
    // return allSubsections;
  }

  makeSubsection(subsection){
    return (
      <Accordion>
        <Card style={{width: '100%', marginTop: '5px', marginBottom: '5px'}}>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="0">{subsection}
              {/* add to cart fuctionality */}
              <Button style={{position: "absolute",right: "5px", padding: "5px"}} variant="outline-primary">Add to Cart</Button>
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body className="mb-2 text-muted">
              Location: {subsection.location} <br></br> 
              Time: {subsection.time} <br></br> 
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    )
  }
}

export default Course;
